using Fsharp.Solutions;
using Microsoft.FSharp.Collections;

namespace Inputs;

public class Input11 : AbstractInput<FSharpList<Tuple<long, long>>, FSharpList<Tuple<long, long>>>
{
    protected override FSharpList<Tuple<long, long>> ParseInput(string input)
    {
        var lines = input.Split('\n');
        var galaxies = new List<Tuple<long, long>>();
        for (var y = 0; y < lines.Length; y++)
        {
            var line = lines[y].ToCharArray();
            for (var x = 0; x < line.Length; x++)
            {
                if (line[x] == '#')
                {
                    galaxies.Add(new Tuple<long, long>(x, y));
                }
            }
        }

        return ListModule.OfSeq(galaxies);
    }

    protected override FSharpList<Tuple<long, long>> ParseInputTwo(string input) => ParseInput(input);

    protected override long SolveFirstPuzzle(FSharpList<Tuple<long, long>> input) => Day11.firstPuzzle(input);

    protected override long SolveSecondPuzzle(FSharpList<Tuple<long, long>> input) => Day11.secondPuzzle(input);

